create table if not exists chat
(
    id          uuid PRIMARY KEY,
    name_chat   text,
    chat_id     uuid,
    recipient   uuid
);

create table if not exists message
(
    id          UUID PRIMARY KEY,
    sender_id    UUID,
    text_message text,
    date_Time_Sending_Message timestamptz
);

create table if not exists recipient
(
    id           UUID PRIMARY KEY,
    recipient_id  UUID
);




